"skills" : {...},
"integrations" : {
"voice_telephony": {
"private":{
"user_phone_number":"+18594213456",
},
"sip_call_id": "Aob2-2743-5678-1234",
"assistant_phone_number":"+18882346789",
"sip_custom_invite_headers": {
"X-customer-name": "my_name",
